package com.testproj.pdfreader.base64modification;

import com.itextpdf.kernel.pdf.*;
import com.itextpdf.layout.Document;
import com.itextpdf.layout.element.Paragraph;

import java.io.ByteArrayInputStream;
import java.io.ByteArrayOutputStream;
import java.io.IOException;
import java.util.Base64;

public class PdfManipulationUtil {

    public static String modifyPdfContents(String base64Pdf) throws IOException {
        // Decode base64 PDF content
        byte[] pdfBytes = Base64.getDecoder().decode(base64Pdf);
        
        // Create input and output streams
        ByteArrayInputStream inputStream = new ByteArrayInputStream(pdfBytes);
        ByteArrayOutputStream outputStream = new ByteArrayOutputStream();
        
        // Open PDF document
        PdfReader reader = new PdfReader(inputStream);
        PdfWriter writer = new PdfWriter(outputStream);
        PdfDocument pdfDoc = new PdfDocument(reader, writer);
        
        // Add new page with custom content
        pdfDoc.addNewPage();
        Document document = new Document(pdfDoc);
        document.add(new Paragraph("PDF Has been modified"));
        
        // Close PDF document
        document.close();
        pdfDoc.close();
        writer.close();
        reader.close();
        
        // Encode modified PDF content to base64
        byte[] modifiedPdfBytes = outputStream.toByteArray();
        String modifiedBase64Pdf = Base64.getEncoder().encodeToString(modifiedPdfBytes);
        
        return modifiedBase64Pdf;
    }

    public static void main(String[] args) throws IOException {
        String base64InputPdf = ""; // Replace with actual base64 input PDF
        String modifiedBase64Pdf = modifyPdfContents(base64InputPdf);
        
        System.out.println("Modified Base64 PDF: " + modifiedBase64Pdf);
    }
}
